import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Subject } from 'rxjs';

import { ClrDatagridComparatorInterface } from '../interfaces/comparator.interface';
import { StateDebouncer } from './state-debouncer.provider';

@Injectable()
export class Sort<T = any> {
  constructor(private stateDebouncer: StateDebouncer) {}

  /**
   * Currently active comparator
   */
  private _comparator: ClrDatagridComparatorInterface<T>;
  public get comparator(): ClrDatagridComparatorInterface<T> {
    return this._comparator;
  }
  public set comparator(value: ClrDatagridComparatorInterface<T>) {
    this.stateDebouncer.changeStart();
    this._comparator = value;
    this.emitChange();
    this.stateDebouncer.changeDone();
  }

  /**
   * Ascending order if false, descending if true
   */
  private _reverse: boolean = false;
  public get reverse(): boolean {
    return this._reverse;
  }
  public set reverse(value: boolean) {
    this.stateDebouncer.changeStart();
    this._reverse = value;
    this.emitChange();
    this.stateDebouncer.changeDone();
  }

  /**
   * The Observable that lets other classes subscribe to sort changes
   */
  private _change = new Subject<Sort<T>>();
  private emitChange() {
    this._change.next(this);
  }
  // We do not want to expose the Subject itself, but the Observable which is read-only
  public get change(): Observable<Sort<T>> {
    return this._change.asObservable();
  }

  /**
   * Sets a comparator as the current one, or toggles reverse if the comparator is already used. The
   * optional forceReverse input parameter allows to override that toggling behavior by sorting in
   * reverse order if `true`.
   *
   * @memberof Sort
   */
  public toggle(sortBy: ClrDatagridComparatorInterface<T>, forceReverse?: boolean) {
    this.stateDebouncer.changeStart();
    // We modify private properties directly, to batch the change event
    if (this.comparator === sortBy) {
      this._reverse = typeof forceReverse !== 'undefined' ? forceReverse || !this._reverse : !this._reverse;
    } else {
      this._comparator = sortBy;
      this._reverse = typeof forceReverse !== 'undefined' ? forceReverse : false;
    }
    this.emitChange();
    this.stateDebouncer.changeDone();
  }

  /**
   * Clears the current sorting order
   */
  public clear() {
    this.comparator = null;
  }

  /**
   * Compares two objects according to the current comparator
   */
  public compare(a: T, b: T): number {
    return (this.reverse ? -1 : 1) * this.comparator.compare(a, b);
  }
}

import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Subject } from 'rxjs';

/*
 * This provider implements some form of synchronous debouncing through a lock pattern
 * to avoid emitting multiple state changes for a single user action.
 */
@Injectable()
export class StateDebouncer {
  /**
   * The Observable that lets other classes subscribe to global state changes
   */
  private _change = new Subject<void>();
  // We do not want to expose the Subject itself, but the Observable which is read-only
  public get change(): Observable<void> {
    return this._change.asObservable();
  }

  /*
     * This is the lock, to only emit once all the changes have finished processing
     */
  private nbChanges = 0;

  public changeStart() {
    this.nbChanges++;
  }

  public changeDone() {
    if (--this.nbChanges === 0) {
      this._change.next();
    }
  }
}

import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

import { DatagridPropertyComparator } from '../built-in/comparators/datagrid-property-comparator';
import { ClrDatagridStateInterface } from '../interfaces/state.interface';

import { FiltersProvider } from './filters';
import { Page } from './page';
import { Sort } from './sort';
import { StateDebouncer } from './state-debouncer.provider';

/**
 * This provider aggregates state changes from the various providers of the Datagrid
 */
@Injectable()
export class StateProvider<T> {
  constructor(
    private filters: FiltersProvider<T>,
    private sort: Sort<T>,
    private page: Page,
    private debouncer: StateDebouncer
  ) {}

  /**
   * The Observable that lets other classes subscribe to global state changes
   */
  change: Observable<ClrDatagridStateInterface<T>> = this.debouncer.change.pipe(map(() => this.state));

  /*
     * By making this a getter, we open the possibility for a setter in the future.
     * It's been requested a couple times.
     */
  get state(): ClrDatagridStateInterface<T> {
    const state: ClrDatagridStateInterface<T> = {};
    if (this.page.size > 0) {
      state.page = {
        from: this.page.firstItem,
        to: this.page.lastItem,
        size: this.page.size,
        current: this.page.current,
      };
    }
    if (this.sort.comparator) {
      if (this.sort.comparator instanceof DatagridPropertyComparator) {
        /*
                 * Special case for the default object property comparator,
                 * we give the property name instead of the actual comparator.
                 */
        state.sort = { by: (<DatagridPropertyComparator<T>>this.sort.comparator).prop, reverse: this.sort.reverse };
      } else {
        state.sort = { by: this.sort.comparator, reverse: this.sort.reverse };
      }
    }

    const activeFilters = this.filters.getActiveFilters();
    if (activeFilters.length > 0) {
      state.filters = [];
      for (const filter of activeFilters) {
        if (filter.state) {
          state.filters.push(filter.state);
        } else {
          state.filters.push(filter);
        }
      }
    }
    return state;
  }
}

import { Injectable } from '@angular/core';
import { map, distinctUntilChanged } from 'rxjs/operators';
import { Observable, Subject } from 'rxjs';

import { AccordionService } from './../../providers/accordion.service';
import { StepperModel } from '../models/stepper.model';

@Injectable()
export class StepperService extends AccordionService {
  readonly panelsCompleted = this.getAllCompletedPanelChanges();
  protected accordion = new StepperModel();

  private _activeStepChanges = new Subject<string>();
  readonly activeStep = this._activeStepChanges.asObservable();

  resetPanels() {
    this.accordion.resetPanels();
    this.emitUpdatedPanels();
  }

  setPanelsWithErrors(ids: string[]) {
    this.accordion.setPanelsWithErrors(ids);
    this.emitUpdatedPanels();
  }

  navigateToNextPanel(currentPanelId: string, currentPanelValid = true) {
    this.accordion.navigateToNextPanel(currentPanelId, currentPanelValid);
    this.updateNextStep(currentPanelId, currentPanelValid);
    this.emitUpdatedPanels();
  }

  overrideInitialPanel(panelId: string) {
    this.accordion.overrideInitialPanel(panelId);
    this.emitUpdatedPanels();
  }

  private updateNextStep(currentPanelId: string, currentPanelValid: boolean) {
    const nextPanel = this.accordion.getNextPanel(currentPanelId);

    if (currentPanelValid && nextPanel) {
      this._activeStepChanges.next(nextPanel.id);
    } else if (currentPanelValid) {
      this._activeStepChanges.next(currentPanelId);
    }
  }

  private getAllCompletedPanelChanges(): Observable<boolean> {
    return this._panelsChanges.pipe(map(() => this.accordion.allPanelsCompleted), distinctUntilChanged());
  }
}

import { isPlatformBrowser } from '@angular/common';
