import { ChangeDetectorRef, Directive, Optional } from '@angular/core';

import { OompaLoompa } from '../../utils/chocolate/oompa-loompa';
import { AccordionWillyWonka } from './accordion-willy-wonka';
import { IfExpandService } from '../../utils/conditional/if-expanded.service';

@Directive({ selector: 'clr-accordion-panel' })
export class AccordionOompaLoompa extends OompaLoompa {
  private expand: IfExpandService;

  constructor(cdr: ChangeDetectorRef, @Optional() willyWonka: AccordionWillyWonka, ifExpandService: IfExpandService) {
    if (!willyWonka) {
      throw new Error('clr-accordion-panel should only be used inside of clr-accordion');
    }
    super(cdr, willyWonka);
    this.expand = ifExpandService;
  }

  get flavor() {
    return this.expand.expanded;
  }
}

import { Directive } from '@angular/core';

import { WillyWonka } from '../../utils/chocolate/willy-wonka';

@Directive({ selector: 'clr-accordion' })
export class AccordionWillyWonka extends WillyWonka {}

import { ChangeDetectorRef, Directive, Optional } from '@angular/core';
import { OompaLoompa } from '../../../utils/chocolate/oompa-loompa';
import { RowActionService } from '../providers/row-action-service';
import { DatagridWillyWonka } from './datagrid-willy-wonka';

@Directive({ selector: 'clr-datagrid, clr-dg-row' })
export class ActionableOompaLoompa extends OompaLoompa {
  private rowActions: RowActionService;

  constructor(cdr: ChangeDetectorRef, @Optional() willyWonka: DatagridWillyWonka, rowActions: RowActionService) {
    if (!willyWonka) {
      throw new Error('clr-dg-row should only be used inside of a clr-datagrid');
    }
    super(cdr, willyWonka);
    this.rowActions = rowActions;
  }

  get flavor() {
    return this.rowActions.hasActionableRow;
  }
}

import { ChangeDetectorRef, Directive, Inject, Optional } from '@angular/core';

import { OompaLoompa } from '../../../utils/chocolate/oompa-loompa';
import { IF_ACTIVE_ID, IfActiveService } from '../../../utils/conditional/if-active.service';

import { TabsWillyWonka } from './tabs-willy-wonka';

@Directive({ selector: '[clrTabLink], clr-tab-content' })
export class ActiveOompaLoompa extends OompaLoompa {
  private ifActive: IfActiveService;
  private id: number;

  constructor(
    cdr: ChangeDetectorRef,
    @Optional() willyWonka: TabsWillyWonka,
    @Inject(IF_ACTIVE_ID) id: number,
    ifActive: IfActiveService
  ) {
    if (!willyWonka) {
      throw new Error('clrTabLink and clr-tab-content should only be used inside of a clr-tabs');
    }
    super(cdr, willyWonka);
    this.ifActive = ifActive;
    this.id = id;
  }

  get flavor() {
    return this.ifActive.current === this.id;
  }
}

