    //     this.subscriptions.push(
    //       this.formGroup.statusChanges.subscribe(() => {
    //         this.invalid = this.formGroup.invalid;
    //       })
    //     );
    //   });
    // }
  }

  controlClass() {
    return this.controlClassService.controlClass(this.invalid, this.addGrid(), this.inline ? 'clr-control-inline' : '');
  }

  addGrid() {
    return this.layoutService && !this.layoutService.isVertical();
  }

  ngOnDestroy() {
    this.subscriptions.map(sub => sub.unsubscribe());
  }
}

import { Component, OnInit, ContentChild, Inject, InjectionToken, OnDestroy } from '@angular/core';
import { BehaviorSubject, Subscription } from 'rxjs';

import { DynamicWrapper } from '../../utils/host-wrapping/dynamic-wrapper';
import { ControlIdService } from '../common/providers/control-id.service';
import { ClrLabel } from '../common/label';

export const IS_TOGGLE = new InjectionToken<BehaviorSubject<boolean>>('IS_TOGGLE');
export function isToggleFactory() {
  return new BehaviorSubject<boolean>(false);
}
export const IS_TOGGLE_PROVIDER = { provide: IS_TOGGLE, useFactory: isToggleFactory };

@Component({
  selector: 'clr-checkbox-wrapper,clr-toggle-wrapper',
  template: `
    <ng-content select="[clrCheckbox],[clrToggle]"></ng-content>
    <ng-content select="label"></ng-content>
    <label *ngIf="!label"></label>
  `,
  host: {
    '[class.clr-checkbox-wrapper]': '!toggle',
    '[class.clr-toggle-wrapper]': 'toggle',
  },
  providers: [ControlIdService, IS_TOGGLE_PROVIDER],
})
export class ClrCheckboxWrapper implements DynamicWrapper, OnInit, OnDestroy {
  // We need both _dynamic for HostWrapper and ContentChild(ClrLabel) in cases where
  // the user puts a radio inside a wrapper without a label, host wrapping doesn't apply
  // but we'd still need to insert a label
  _dynamic = false;
  @ContentChild(ClrLabel, { static: true })
  label: ClrLabel;
  toggle = false;
  private subscriptions: Subscription[] = [];

  constructor(@Inject(IS_TOGGLE) toggleService: BehaviorSubject<boolean>) {
    this.subscriptions.push(
      toggleService.subscribe(state => {
        this.toggle = state;
      })
    );
  }

  ngOnInit() {
    if (this.label) {
      this.label.disableGrid();
    }
  }

  ngOnDestroy() {
    this.subscriptions.forEach(sub => sub.unsubscribe());
  }
}

import { isPlatformBrowser } from '@angular/common';
import {
  AfterContentInit,
  Component,
  ContentChild,
  ElementRef,
  HostListener,
  OnDestroy,
  PLATFORM_ID,
  Renderer2,
  ViewChild,
} from '@angular/core';
import { Subscription } from 'rxjs';

import { POPOVER_HOST_ANCHOR } from '../../popover/common/popover-host-anchor.token';
import { ClrPopoverToggleService } from '../../utils/popover/providers/popover-toggle.service';
import { TAB } from '../../utils/key-codes/key-codes';

import { ClrOption } from './option';
import { ClrOptions } from './options';
import { OptionSelectionService } from './providers/option-selection.service';
import { ComboboxDomAdapter } from './utils/combobox-dom-adapter.service';
import { ComboboxNoopDomAdapter } from './utils/combobox-noop-dom-adapter.service';

// Fixes build error
// @dynamic (https://github.com/angular/angular/issues/19698#issuecomment-338340211)
export function comboboxDomAdapterFactory(platformId: Object) {
  if (isPlatformBrowser(platformId)) {
    return new ComboboxDomAdapter();
  } else {
    return new ComboboxNoopDomAdapter();
  }
}

@Component({
  selector: 'clr-combobox',
  templateUrl: './combobox.html',
  providers: [
    ClrPopoverToggleService,
    { provide: POPOVER_HOST_ANCHOR, useExisting: ElementRef },
    OptionSelectionService,
    { provide: ComboboxDomAdapter, useFactory: comboboxDomAdapterFactory, deps: [PLATFORM_ID] },
  ],
  host: { '[class.clr-combobox]': 'true' },
})
export class ClrCombobox<T> implements AfterContentInit, OnDestroy {
  @ViewChild('input', { static: false })
  input: ElementRef;
  @ContentChild(ClrOptions, { static: false })
  options: ClrOptions;
  private subscription: Subscription;

  constructor(
    private toggleService: ClrPopoverToggleService,
    private optionSelectionService: OptionSelectionService<T>,
    private renderer: Renderer2,
    private domAdapter: ComboboxDomAdapter
  ) {
    // TODO: Might need to delay this to later in the lifecycle and use a BehaviorSubject instead
    this.initializeSubscriptions();
  }

  private initializeSubscriptions(): void {
    this.subscription = this.optionSelectionService.renderSelectionChanged.subscribe((option: ClrOption<T>) => {
      this.renderSelection(option);
    });
  }

  private renderSelection(selectedOption: ClrOption<T>): void {
    if (this.input && selectedOption) {
      this.domAdapter.clearChildren(this.input.nativeElement);
      const clone: HTMLElement = this.domAdapter.cloneNode(selectedOption.elRef.nativeElement);
      this.renderer.setAttribute(clone, 'contenteditable', 'false');
      this.renderer.appendChild(this.input.nativeElement, clone);
    }
  }

  private registerPopoverIgnoredInput() {
    if (this.input) {
      // @TODO COMBOBOX: intentionally commented; resolve while merging the Combobox
      //this.toggleService.registerIgnoredElement(this.input);
    }
  }

  toggleOptionsMenu(event: MouseEvent): void {
    this.toggleService.toggleWithEvent(event);
  }

  @HostListener('click')
  focusInput() {
    if (this.input) {
      this.domAdapter.focus(this.input.nativeElement);
    }
  }

  closeMenuOnTabPress(event: KeyboardEvent) {
    if (event && event.keyCode === TAB) {
      this.toggleService.open = false;
    }
  }

  // Lifecycle methods
  ngAfterContentInit() {
    this.registerPopoverIgnoredInput();
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }
}

