import { User } from './user';
import { BEGINNING, COLORS, NAMES, NOW, POKEMON } from './values';

export class Inventory {
  public size = 100;
  public latency = 0;

  private _all: User[];
  private _currentQuery: User[];

  get all(): User[] {
    return this._all.slice();
  }

  reset() {
    this._all = [];
    for (let i = 0; i < this.size; i++) {
      this._all.push({
        id: randomInt(100000),
        name: NAMES[randomInt(NAMES.length)],
        creation: new Date(BEGINNING + randomInt(NOW - BEGINNING)),
        color: COLORS[randomInt(COLORS.length)],
        pokemon: POKEMON[randomInt(POKEMON.length)],
        wins: randomInt(100),
      });
    }
  }

  private _checkCurrentQuery() {
    if (!this._currentQuery) {
      this._currentQuery = this._all.slice();
    }
  }

  filter(filters: { [key: string]: string[] }): Inventory {
    this._checkCurrentQuery();
    if (filters) {
      for (const key in filters) {
        if (filters[key].length === 0) {
          continue;
        }

        let getFilterProperty = (user: User) => '' + user[key];
        if (key === 'pokemon') {
          getFilterProperty = (user: User) => user.pokemon.name;
        }

        const lowerCase = filters[key].map(value => value.toLowerCase());
        this._currentQuery = this._currentQuery.filter(user => {
          for (const value of lowerCase) {
            if (
              getFilterProperty(user)
                .toLowerCase()
                .indexOf(value) >= 0
            ) {
              return true;
            }
          }
          return false;
        });
      }
    }
    return this;
  }

  sort(sort: { by: string; reverse: boolean }): Inventory {
    this._checkCurrentQuery();
    if (sort && sort.by) {
      let getSortProperty = (user: User) => user[sort.by];
      if (sort.by === 'pokemon') {
        getSortProperty = (user: User) => user.pokemon.number;
      }

      this._currentQuery.sort((a, b) => {
        let comp = 0;
        const propA = getSortProperty(a),
          propB = getSortProperty(b);
        if (propA < propB) {
          comp = -1;
        } else if (propA > propB) {
          comp = 1;
        }
        if (sort.reverse) {
          comp = -comp;
        }
        return comp;
      });
    }
    return this;
  }

  fetch(skip: number = 0, limit: number = this._currentQuery.length): Promise<FetchResult> {
    const result: FetchResult = {
      users: this._currentQuery.slice(skip, skip + limit),
      length: this._currentQuery.length,
    };
    this._currentQuery = null;
    return this._fakeHttp(result);
  }

  private _fakeHttp<T>(result: T): Promise<T> {
    return new Promise((resolve, reject) => {
      setTimeout(() => resolve(result), this.latency);
    });
  }
}

export interface FetchResult {
  users: User[];
  length: number;
}

function randomInt(max: number): number {
  return Math.floor(Math.random() * max);
}

import { Component, OnInit } from '@angular/core';
import { Observable, of, timer } from 'rxjs';
import { map } from 'rxjs/operators';

const EXAMPLE_HTML = `
<clr-tree [clrLazy]="true">
    <clr-tree-node *clrRecursiveFor="let file of root$ | async; getChildren: getChildren"
                   [clrExpandable]="file.isFolder">
        <clr-icon [attr.shape]="file.isFolder ? 'folder' : 'file'"></clr-icon>
        {{file.name}}
    </clr-tree-node>
</clr-tree>
`;

const EXAMPLE_TS = `
@Component({...})
export class FileBrowser implements OnInit {
    constructor(private folderService: FolderService) {}

    root$: Observable<File[]>;

    getChildren = (folder: File) => {
        if (folder.isFolder) {
            return this.folderService.getFiles(folder);
        } else {
            return null;
        }
    };

    ngOnInit(): void {
        this.root$ = this.folderService.getFiles('/');
    }
}
`;

interface File {
  name: string;
  isFolder: boolean;
  files: File[];
}

const ROOT = [
  {
    name: 'src',
    isFolder: true,
    files: [
      {
        name: 'app',
        isFolder: true,
        files: [
          {
            name: 'app.component.html',
            isFolder: false,
          },
          {
            name: 'app.component.ts',
            isFolder: false,
          },
          {
            name: 'app.module.ts',
            isFolder: false,
          },
          {
            name: 'app.routing.ts',
            isFolder: false,
          },
        ],
      },
      {
        name: 'environments',
        isFolder: true,
        files: [
          {
            name: 'environments.prod.ts',
            isFolder: false,
          },
          {
            name: 'environment.ts',
            isFolder: false,
          },
        ],
      },
      {
        name: 'index.html',
        isFolder: false,
      },
      {
        name: 'main.ts',
        isFolder: false,
      },
    ],
  },
  {
    name: 'package.json',
    isFolder: false,
  },
  {
    name: 'tsconfig.json',
    isFolder: false,
  },
];

@Component({
  selector: 'clr-lazy-loading-recursive-tree-demo',
  // Note the .css extension here, not .scss. That's the best we can have at the moment.
  styleUrls: ['../tree-view.demo.scss'],
  templateUrl: './lazy-loading-recursive-tree.html',
})
export class LazyLoadingRecursiveTreeDemo implements OnInit {
  exampleHtml = EXAMPLE_HTML;
  exampleTs = EXAMPLE_TS;

  root$: Observable<File[]>;

  getChildren = (folder: File) => {
    if (folder.isFolder) {
      return this.folderService.getFiles(folder);
    } else {
      return null;
    }
  };

  ngOnInit(): void {
    this.root$ = this.folderService.getFiles('/');
  }

  folderService = {
    getFiles: folder => {
      if (folder === '/') {
        return of(ROOT);
      } else {
        return timer(1000).pipe(map(() => folder.files));
      }
    },
  };
}

import { ClrSelectedState } from '@clr/angular';
import { of, timer } from 'rxjs';
import { map } from 'rxjs/operators';

export interface Group {
  name: string;
  selected: ClrSelectedState;
}

export interface Item {
  name: string;
  selected: ClrSelectedState;
}

class GroupImpl {
  constructor(public name: string, private _selected: ClrSelectedState, public items: Item[]) {}

  get selected() {
    return this._selected;
  }
  set selected(value: ClrSelectedState) {
    this._selected = value;
    if (value !== ClrSelectedState.INDETERMINATE) {
      this.items.forEach(item => (item.selected = value));
    }
  }
}

const ALL_GROCERIES = [
  new GroupImpl('Dairy', ClrSelectedState.INDETERMINATE, [
    {
      name: 'Milk',
      selected: ClrSelectedState.UNSELECTED,
    },
    {
      name: 'Cheese',
      selected: ClrSelectedState.SELECTED,
    },
  ]),
  new GroupImpl('Vegetables', ClrSelectedState.UNSELECTED, [
    {
      name: 'Carrots',
      selected: ClrSelectedState.UNSELECTED,
    },
    {
      name: 'Potatoes',
      selected: ClrSelectedState.UNSELECTED,
    },
    {
      name: 'Beans',
      selected: ClrSelectedState.UNSELECTED,
    },
  ]),
];

export const GROCERY_SERVICE = {
  getGroups: () => of(ALL_GROCERIES),
};

export const ITEMS_SERVICE = {
  getItems: (group: Group) => timer(1000).pipe(map(() => (<any>group).items)),
};

