import { Directive, ElementRef, Inject, Input, PLATFORM_ID, Renderer2 } from '@angular/core';

declare var Prism: any;

@Directive({ selector: 'code[clr-code-highlight]' })
export class CodeHighlight {
  private _highlight: string = '';

  // Had to use renderer because I wanted to add to existing classes on the code block
  // Didn't want to override them completely
  constructor(private _el: ElementRef, private renderer: Renderer2, @Inject(PLATFORM_ID) private platformId: Object) {}

  ngAfterContentInit(): void {
    this.redraw();
  }

  public redraw() {
    // Only run Prism in browser engines
    if (this._el && this._el.nativeElement && isPlatformBrowser(this.platformId)) {
      Prism.highlightElement(this._el.nativeElement);
    }
  }

  @Input('clr-code-highlight')
  set highlight(val: string) {
    if (val && val.trim() !== '') {
      this._highlight = val;
      this.renderer.addClass(this._el.nativeElement, this._highlight);
    }
  }

  get highlight(): string {
    return this._highlight;
  }
}

import { Directive, TemplateRef, ViewContainerRef, Input, OnInit } from '@angular/core';
import { ClrLoading } from '@clr/angular';

const LATENCY = 2000;

@Directive({
  selector: '[clrFakeLoader]',
})
export class FakeLoader implements OnInit {
  constructor(private template: TemplateRef<any>, private container: ViewContainerRef, private loading: ClrLoading) {}

  @Input('clrFakeLoader') fake: boolean;

  ngOnInit() {
    if (this.fake) {
      this.loading.loadingState = true;
      setTimeout(() => {
        this.load();
        this.loading.loadingState = false;
      }, LATENCY);
    } else {
      this.load();
    }
  }

  private load() {
    this.container.createEmbeddedView(this.template);
  }
}

import { Directive, ElementRef } from '@angular/core';
import { ActiveFragmentService } from './active-fragment.service';

@Directive({
  selector: '[fragment]',
})
export class FragmentLinkDirective {
  constructor(el: ElementRef, activeFragmentService: ActiveFragmentService) {
    activeFragmentService.fragmentLinks[el.nativeElement.getAttribute('fragment')] = el;
  }
}

import { Directive, OnDestroy, OnInit, PLATFORM_ID, Inject } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { Subscription } from 'rxjs';

@Directive({
  selector: '[hash-listener]',
  host: {
    // '[style.position]': "'relative'",
  },
})
export class HashListener implements OnDestroy, OnInit {
  constructor(private route: ActivatedRoute, @Inject(PLATFORM_ID) private platformId: Object) {
    this.sub = this.route.fragment.subscribe(f => {
      this.scrollToAnchor(f, false);
    });
  }

  sub: Subscription;

  ngOnInit() {
    this.scrollToAnchor(this.route.snapshot.fragment, false);
  }

  scrollToAnchor(hash: string, smooth = true) {
    if (hash && isPlatformBrowser(this.platformId)) {
      const element = document.querySelector('#' + hash);
      if (element) {
        element.scrollIntoView({
          behavior: smooth ? 'smooth' : 'auto',
          block: 'start',
        });
      }
    }
  }

  ngOnDestroy() {
    this.sub.unsubscribe();
  }
}

