import { Component } from '@angular/core';
import { environment } from '../../environments/environment';

import { ClarityIcons } from '@clr/icons';
import { AllShapes } from '@clr/icons/shapes/all-shapes';
import * as COMPONENTS from '../../settings/componentlist.json';

@Component({
  selector: 'documentation',
  templateUrl: 'documentation.component.html',
  host: {
    '[class.content-container]': 'true',
  },
})
export class DocumentationComponent {
  environment = environment;
  components = COMPONENTS.list;
  navExpandedState = {
    pattern: true,
    component: true,
  };

  constructor() {
    ClarityIcons.add(AllShapes);
    const cache = localStorage.getItem('navExpandedCache');
    if (cache) {
      try {
        const state = JSON.parse(cache);
        this.navExpandedState = state;
      } catch (e) {
        // Do nothing, defaults are set to open
      }
    }
    console.log(this.navExpandedState);
  }

  cacheNavState($event, state) {
    this.navExpandedState[state] = $event;
    localStorage.setItem('navExpandedCache', JSON.stringify(this.navExpandedState));
  }
}

import { Component, Input } from '@angular/core';

@Component({
  selector: 'clr-doc-wrapper',
  templateUrl: './doc-wrapper.html',
  host: {
    '[class.dox-wrapper]': 'true',
  },
})
export class DocWrapper {
  @Input() title: string = '';
  @Input() newLayout: boolean = false;
  @Input() ui: number = 0;
  @Input() ng: number = 0;
  @Input() description: string = '';

  get useNewLayout() {
    return !!this.newLayout;
  }

  get uiInProgress() {
    return 4 < this.ui && this.ui < 20;
  }

  get uiDone() {
    return this.ui > 19;
  }

  get ngInProgress() {
    return 4 < this.ng && this.ui < 20;
  }

  get ngDone() {
    return this.ng > 19;
  }
}

