@import '../../../../clr-core/styles/variables/variables.color';
@import '../../styles/variables';
@import '../../styles/mixins';

.layout-home {
  background-color: $clr-color-neutral-50;

  .main-container {
    height: auto;

    .content-area {
      overflow-x: hidden;
      padding: 0;
    }
  }

  $clrweb-home-sectionPadding: $clr_baselineRem_4;

  section {
    padding-top: $clrweb-home-sectionPadding;
    padding-bottom: $clrweb-home-sectionPadding;

    &.home-hero {
      padding-top: $clr_baselineRem_2_5;
      padding-bottom: $clr_baselineRem_3;
    }

    &.home-cards,
    &.home-process {
      padding-top: 0;
    }

    &.home-contact {
      padding: 0;
    }
  }
}

.home-hero {
  height: $clr_baselineRem_29;
  max-width: $clr_baselineRem_52;
  width: 100%;
  margin: 0 auto;
  position: relative;

  .home-hero-content {
    position: relative;
    width: 100%;
    max-width: $clr_baselineRem_27;
    z-index: 1;
  }

  h1 {
    font-weight: 200;
    letter-spacing: -0.02em;
    line-height: $clr_baselineRem_3;
    font-size: $clr_baselineRem_2_66;
    margin-bottom: $clr_baselineRem_0_5;
    margin-top: $clr_baselineRem_0_5;
  }

  p.head {
    font-size: $clr_baselineRem_0_75;
    font-weight: 200;
    line-height: $clr_baselineRem_1_5;
    margin: $clr_baselineRem_0_25 $clr_baselineRem_5px;
    color: $clr-color-neutral-1000;
    max-width: $clr_baselineRem_26;
  }

  .home-hero-btn {
    margin: $clr_baselineRem_1_5 0 $clr_baselineRem_1;
    padding-left: $clr_baselineRem_5px;
  }

  .btn {
    width: $clr_baselineRem_7;
    height: $clr_baselineRem_2;
    line-height: $clr_baselineRem_2 - $clr_baselineRem_1px;
    margin: 0;
    margin-right: $clr_baselineRem_0_75;
    font-weight: 400;
  }

  .btn-secondary {
    margin-right: 0;
  }

  p.subtext {
    margin-left: $clr_baselineRem_5px;

    & > a {
      color: inherit;
      text-decoration: underline;

      &:hover,
      &:active,
      &:focus {
        color: $clr-color-neutral-900;
        text-decoration: underline;
      }
    }
  }

  .bg-img {
    height: $clr_baselineRem_21_67;
    width: $clr_baselineRem_28_875;
    position: absolute;
    bottom: 0;
    left: $clr_baselineRem_25_5;
    background-image: url('../../assets/images/clarity-logo.svg');
    background-size: cover;
    background-position: center center;

    &-spring {
      background-image: url('../../assets/images/clarity-logo-spring.svg');
    }

    &-winter {
      background-image: url('../../assets/images/clarity-logo-winter.svg');
    }
  }
}

.home-cards {
  width: 100%;
  max-width: $clr_baselineRem_52;
  margin: 0 auto;
  z-index: 1;
  position: relative;

  .home-cards-container {
    background-color: rgba($clr-color-neutral-0, 0.9);
    width: 100%;
    padding: $clr_baselineRem_2 0 $clr_baselineRem_1;
    border-radius: $clr-global-borderradius;
    margin-top: $clr_baselineRem_7 * -1;
    border: $clr-global-borderwidth solid $clr-color-neutral-300;

    @include clarityWebBoxShadow;

    & > h1 {
      margin: 0;
      margin-bottom: $clr_baselineRem_2;
      text-align: center;
    }
  }

  .home-card-wrapper {
    width: 100%;
    margin: 0;
  }

  .home-card {
    padding: $clr_baselineRem_1 $clr_baselineRem_1 0 $clr_baselineRem_2;
    text-align: left;
  }

  .home-card-angular {
    padding-right: $clr_baselineRem_1_5;
    padding-left: $clr_baselineRem_1;
  }

  .home-card-title {
    line-height: $clr_baselineRem_2;
    vertical-align: middle;
    margin: $clr_baselineRem_0_5 0 $clr_baselineRem_4px;
    font-weight: 200;
    text-align: center;
  }

  .home-card-bug {
    text-align: center;
  }

  p {
    margin: 0 auto;
    font-size: $clr_baselineRem_0_667;
    line-height: $clr_baselineRem_1_16;
    width: 100%;
    max-width: $clr_baselineRem_10;
  }
}

.home-process {
  width: 100%;
  padding: 0;
  max-width: $clr_baselineRem_40_0833;
  margin: 0 auto;

  h1 {
    margin: 0;
    margin-top: $clr_baselineRem_1;
    margin-bottom: $clr_baselineRem_3;
    text-align: center;
  }

  & > .row {
    margin: 0;
  }

  .home-process-block__title {
    position: relative;

    & > .img {
      height: $clr_baselineRem_1_5;
      width: $clr_baselineRem_1_5;
      position: absolute;
      left: $clr_baselineRem_0_25;
      top: $clr_baselineRem_0_25;
      background-position: center center;
      background-size: contain;
      background-repeat: no-repeat;

      &.bulb {
        background-image: url('../../assets/images/home/ic-bulb.svg');
      }

      &.evolving {
        background-image: url('../../assets/images/home/ic_evolving.svg');
      }

      &.rapid {
        background-image: url('../../assets/images/home/ic_rapid.svg');
      }

      &.reliable {
        background-image: url('../../assets/images/home/ic_reliable.svg');
      }
    }

    & > h3 {
      padding-left: $clr_baselineRem_2_25;
      line-height: $clr_baselineRem_2;
    }
  }
}

.home-process-block {
  padding-left: 0;
  padding-right: 0;

  p {
    width: 100%;
    max-width: $clr_baselineRem_17;
    margin-top: 0;
    margin-bottom: $clr_baselineRem_2;
    font-size: $clr_baselineRem_0_667;
  }

  &:nth-child(2n + 0) {
    padding-left: $clr_baselineRem_1_5;
  }
}

.home-contact {
  width: 100%;
  background: linear-gradient(
    to right,
    $clr-color-action-600 0%,
    $clr-color-action-600 50%,
    $clr-color-neutral-900 50%
  );
}

.home-contact-container,
.layout-home footer {
  max-width: $clr_baselineRem_52;
  width: 100%;
  margin: 0 auto;
}

.home-contact-block {
  padding: 0;

  &:nth-child(2n + 0) {
    padding-left: $clr_baselineRem_1_5;

    a.home-contact_link {
      padding-left: $clr_baselineRem_3 + $clr_baselineRem_0_25;
    }

    .img {
      width: $clr_baselineRem_3;
    }
  }
}

a.home-contact_link {
  display: block;
  line-height: $clr_baselineRem_3;
  margin-top: $clr_baselineRem_4;
  margin-bottom: $clr_baselineRem_1;
  position: relative;
  padding-left: $clr_baselineRem_2_25;

  color: $clr-color-neutral-0;

  & > .img {
    display: block;
    height: $clr_baselineRem_3;
    width: $clr_baselineRem_2;
    position: absolute;
    top: 0;
    left: 0;
  }

  &.mit-link > .img {
    background-image: url('../../assets/images/home/MIT.png');
    background-position: center center;
    background-repeat: no-repeat;
  }

  &.twr-link > .img {
    background-image: url('../../assets/images/home/twitter.svg');
    background-position: center center;
    background-repeat: no-repeat;
  }
}

@media (max-width: 1364px) {
  .home-hero {
    width: 100%;
  }
}

@media (max-width: 1291px) {
  .layout-home {
    section,
    section.home-contact {
      padding-left: $clr_baselineRem_0_75;
      padding-right: $clr_baselineRem_0_75;
    }

    section.home-cards {
      padding-left: $clr_baselineRem_1;
      padding-right: $clr_baselineRem_1;
    }

    section.home-process {
      padding-left: $clr_baselineRem_0_5;
      padding-right: $clr_baselineRem_0_5;
    }

    footer {
      padding-left: $clr_baselineRem_1;
      padding-right: $clr_baselineRem_1;
    }
  }
}

@media (max-width: 1092px) {
  .home-hero {
    .bg-img {
      left: $clr_baselineRem_21;
    }
  }
}

@media (max-width: 991px) {
  .layout-home section.home-hero {
    padding-top: $clr_baselineRem_1;
    padding-bottom: $clr_baselineRem_3;
    height: $clr_baselineRem_27_5;
  }
}

@media (max-width: 907px) {
  .layout-home {
    section.home-process {
      padding-left: $clr_baselineRem_1_75;
      padding-right: $clr_baselineRem_1_75;
    }
  }

  .home-cards {
    .home-cards-container {
      & > h1 {
        font-size: $clr_baselineRem_1_16;
        line-height: $clr_baselineRem_1_5;
        margin-bottom: $clr_baselineRem_1;
      }
    }

    .home-card {
      padding: $clr_baselineRem_1 $clr_baselineRem_0_5 0 $clr_baselineRem_1;
    }

    .home-card-angular {
      padding-right: $clr_baselineRem_0_5;
      padding-left: $clr_baselineRem_0_5;
    }
  }
}

@media (max-width: 768px) {
  .layout-home section {
    &.home-hero {
      padding-top: $clr_baselineRem_1;
      height: $clr_baselineRem_20_5;
      padding-bottom: $clr_baselineRem_1;
    }

    &.home-cards {
      padding-bottom: $clr_baselineRem_1;
    }

    &.home-process {
      padding-left: $clr_baselineRem_1;
      padding-right: $clr_baselineRem_1;
