@Component({
  selector: 'clr-tree-view-dynamic-demo',
  // Note the .css extension here, not .scss. That's the best we can have at the moment.
  styleUrls: ['../tree-view.demo.scss'],
  templateUrl: './tree-view-dynamic.html',
})
export class TreeViewDynamicDemo {
  @Input('clrDemoShowCode') showCode: boolean = false;
  @Input('clrDemoShowHalf') showHalf: boolean = false;

  exampleHtml = EXAMPLE_HTML;
  exampleTs = EXAMPLE_TS;

  rootDirectory: any[] = [
    {
      name: 'Applications',
      icon: 'folder',
      expanded: true,
      files: [
        {
          icon: 'calendar',
          name: 'Calendar',
          active: true,
        },
        {
          icon: 'line-chart',
          name: 'Charts',
          active: false,
        },
        {
          icon: 'dashboard',
          name: 'Dashboard',
          active: false,
        },
        {
          icon: 'map',
          name: 'Maps',
          active: false,
        },
        {
          icon: 'email',
          name: 'Mail',
          active: false,
        },
        {
          icon: 'bar-chart',
          name: 'Numbers',
          active: false,
        },
        {
          icon: 'tasks',
          name: 'Tasks',
          active: false,
        },
        {
          icon: 'flag',
          name: 'Reminders',
          active: false,
        },
      ],
    },
    {
      name: 'Files',
      icon: 'folder',
      expanded: false,
      files: [
        {
          icon: 'file',
          name: 'Cover Letter.doc',
          active: false,
        },
        {
          icon: 'file',
          name: 'Flyer.doc',
          active: false,
        },
        {
          icon: 'file',
          name: 'Resume.doc',
          active: false,
        },
        {
          icon: 'file',
          name: 'Notes.txt',
          active: false,
        },
      ],
    },
    {
      name: 'Images',
      icon: 'folder',
      expanded: false,
      files: [
        {
          icon: 'image',
          name: 'Screenshot.png',
          active: false,
        },
        {
          icon: 'image',
          name: 'Pic.png',
          active: false,
        },
        {
          icon: 'image',
          name: 'Portfolio.jpg',
          active: false,
        },
      ],
    },
  ];

  openFile(directoryName, fileName): void {
    for (const dir of this.rootDirectory) {
      if (directoryName === dir.name) {
        this.setFileActive(dir, fileName);
      } else {
        for (const file of dir.files) {
          file.active = false;
        }
      }
    }
  }

  setFileActive(dir: any, fileName: string) {
    for (const file of dir.files) {
      if (file.name === fileName) {
        file.active = true;
      } else {
        file.active = false;
      }
    }
  }
}

